/* eslint-disable react/prop-types */
import { CgClose } from "react-icons/cg";
import { Wallet } from "lucide-react";

import safePng from "../../../assets/safepal.png";
import trustPng from "../../../assets/trustwallet.jpg";
import tokenPocketPng from "../../../assets/tokenpocket.png";

const WalletOptionModal = ({ show, hide, connectWallet }) => {
  if (!show) return null;

  const walletOptions = [
    { name: "MetaMask", key: "metamask", icon: "https://img.icons8.com/color/96/metamask-logo.png" },
    { name: "Trust Wallet", key: "trustwallet", icon: trustPng },
    { name: "SafePal", key: "safepal", icon: safePng },
    { name: "Token Pocket", key: "tokenpocket", icon: tokenPocketPng },
    { name: "Math Wallet", key: "mathwallet", icon: "data:image/png;base64," },
    { name: "Coin98", key: "coin98", icon: "data:image/jpeg;base64," },
    { name: "Binance Web3", key: "binanceweb3", icon: "data:image/png;base64," },
    { name: "OKX Wallet", key: "okx", icon: "data:image/png;base64," },
    { name: "Bitget Wallet", key: "bitget", icon: "data:image/jpeg;base64," },
    { name: "WalletConnect", key: "walletconnect", icon: "data:image/webp;base64," },
  ];

  const selectHandler = (walletKey) => {
    connectWallet(walletKey);
    hide();
  };

  return (
    <div className="fixed inset-0 z-50 bg-black/60 flex items-center justify-center">
      <div className="relative w-full max-w-lg mx-4 bg-[#020617] rounded-2xl shadow-2xl p-6 border border-blue-500/20">

        {/* Close */}
        <button
          onClick={hide}
          className="absolute top-4 right-4 text-gray-400 hover:text-red-500 text-2xl"
        >
          <CgClose />
        </button>

        {/* Info Banner */}
        <div className="mb-6 rounded-lg border border-blue-500/30 bg-blue-500/10 px-4 py-3 flex items-start gap-3">
          <Wallet className="w-5 h-5 text-blue-400 mt-0.5" />
          <p className="text-xs text-blue-300 leading-relaxed">
            <span className="font-semibold text-blue-400">Wallet System</span>{" "}
            <br />
            <span className="text-blue-400 font-medium">
              Popular DApps Wallet
            </span>
          </p>
        </div>

        {/* Title */}
        <h2 className="text-xl font-bold text-center text-white mb-5">
          Choose Wallet
        </h2>

        {/* âœ… GRID 2 */}
        <div className="grid grid-cols-2 gap-4">
          {walletOptions.map((option) => (
            <button
              key={option.key}
              onClick={() => selectHandler(option.key)}
              className="flex items-center gap-3 p-3 rounded-xl
              border border-gray-700/60
              hover:border-blue-500/60
              hover:bg-blue-500/5
              transition-all duration-200"
            >
              <img
                src={option.icon}
                alt={option.name}
                className="w-8 h-8 object-contain"
              />
              <span className="text-gray-200 text-sm font-medium">
                {option.name}
              </span>
            </button>
          ))}
        </div>

        {/* Footer */}
        <p className="mt-5 text-center text-xs text-gray-400">
          Secure wallet connection
        </p>
      </div>
    </div>
  );
};

export default WalletOptionModal;
